/**
 * Get platform icon metadata synchronously (for colors, etc.)
 * Use this when you only need the hex color and don't need the full icon
 */
export function getPlatformIconMetadata(
  platform: string
): { hex: string } | undefined {
  const entry = ICON_DATA[platform.toLowerCase()];
  return entry ? { hex: entry.hex } : undefined;
}

export async function getPlatformIcon(
  platform: string
): Promise<{ path: string; hex: string; slug: string } | undefined> {
  const entry = ICON_DATA[platform.toLowerCase()];
  if (!entry) return undefined;
  return { path: entry.path, hex: entry.hex, slug: platform.toLowerCase() };
}

function SocialIconInner({
  platform,
  className,
  size,
  'aria-hidden': ariaHidden = true,
  'aria-label': ariaLabel,
}: Readonly<SocialIconProps>) {
  const iconClass = className || 'h-4 w-4';
  const sizeStyle = size ? { width: size, height: size } : undefined;
  const entry = ICON_DATA[platform.toLowerCase()];

  if (!entry) {
    // Fallback for unknown platforms
    return (
      <svg
        className={iconClass}
        style={sizeStyle}
        fill='none'
        stroke='currentColor'
        viewBox='0 0 24 24'
        aria-hidden='true'
      >
        <path
          strokeLinecap='round'
          strokeLinejoin='round'
          strokeWidth={2}
          d='M13.828 10.172a4 4 0 00-5.656 0l-4 4a4 4 0 105.656 5.656l1.102-1.101m-.758-4.899a4 4 0 005.656 0l4-4a4 4 0 00-5.656-5.656l-1.1 1.1'
        />
      </svg>
    );
  }

  return (
    // SVG requires role="img" for accessibility; native <img> cannot render inline SVGs
    <svg
      className={iconClass}
      style={sizeStyle}
      fill='currentColor'
      viewBox='0 0 24 24'
      aria-hidden={ariaHidden}
      aria-label={ariaLabel}
      role={ariaLabel ? 'img' : undefined}
    >
      <path d={entry.path} />
    </svg>
  );
}

export const SocialIcon = memo(SocialIconInner);
